import React, { Component, Fragment}  from 'react';
import ExerciseList from '../components/ExerciseList'
import Welcome from '../components/Welcome';
import Loading from '../components/Loading';
import FatalError from './500';


class Exercices extends Component{
    state ={
        data: [],
        loading: true,
        error: null    
    }

    async componentDidMount(){
        await this.fetchExercise();
    }
    fetchExercise = async () =>{
        try{
            let res = await fetch('http://localhost:4000/api/exercise');
            let data = await res.json()
            this.setState({ 
                data, 
                loading:false 
            })
        }
        catch (error) {
            this.setState({ 
                loading:false,
                error
            })
        }
    }

    render(){
        if (this.state.loading)
            return <Loading className="centrado"/>
        if (this.state.error)
            return <FatalError />
        return(
            <Fragment className="contenedor">
                <Welcome 
                    username="David"
                />
                <ExerciseList className="center"
                     exercises={this.state.data}                   
                />
            </Fragment>
        )
    }
}
export default Exercices
